    // }

    // removeIngredientHandler = (type)=>{
    //     const oldCount = this.state.ingredients[type];
    //     if(oldCount <= 0){
    //         return;
    //     }
    //     const updatedCount = oldCount - 1;
    //     const updatedIngredients = {
    //         ...this.state.ingredients
    //     };
    //     updatedIngredients[type] = updatedCount;
    //     const priceDeduction = INGREDIENT_PRICES[type];
    //     const oldPrice = this.state.totalPrice;
    //     const newPrice = oldPrice-priceDeduction;
    //     this.setState({totalPrice:newPrice,ingredients:updatedIngredients});
    //     this.updatePurchaseState(updatedIngredients);
    // }
    purchaseHandler=()=>{
        this.setState({purchasing:true});
    }
    purchaseCancelHandler=()=>{
        this.setState({purchasing:false});
    }
    purchaseContinueHandler = () =>{
        //alert('You continue!');
        // this.setState({loading:true});
        // const order ={
        //     ingredients:this.state.ingredients,
        //     price:this.state.totalPrice,
        //     customer:{
        //         name:'Vishu Saxena',
        //         address:{
        //             street:'Street -1 New Bay',
        //             zipcode:'564646',
        //             country:'INDIA'
        //         },
        //     email:'vishu@gmail.com',
        //     deliveryMethod:'fastest'
        //     }
        // }
        // axios.post('/orders.json',order)
        // .then(response=>{
        //     console.log(response);
        //     this.setState({loading:false,purchasing:false});
        // })
        // .catch(err=>{
        //     this.setState({loading:false,purchasing:false});
        //     console.log(err);
        // });  
        const queryParams =[];
        for(let i in this.state.ingredients){
            queryParams.push(encodeURIComponent(i)+'='+encodeURIComponent(this.state.ingredients[i]));
        }
        queryParams.push('price='+this.state.totalPrice);
        const queryString = queryParams.join('&');
        this.props.history.push({
            pathname:'/checkout',
            search:'?'+queryString
        });
    }
    render(){
        const disabledInfo = {
            ...this.props.ings
        };
        for(let key in disabledInfo){
            disabledInfo[key] = disabledInfo[key] <=0;
        }
        let Ordersummary = null;
        let burger =this.state.error?<p>Ingredients can't be loaded!</p>:<Spinner />;
        if(this.props.ings){
            burger =(<Aux>
                <Burger ingredients={this.props.ings}/>
                <BuildControls 
                ingredientAdded={this.props.onIngredientAdded}
                ingredientRemoved={this.props.onIngredientRemoved}
                disabled={disabledInfo}
                purchaseable={this.state.purchaseable}
                ordered={this.purchaseHandler}
                price={this.state.totalPrice}/>
            </Aux>
            );
            Ordersummary = (<OrderSummary ingredients={this.props.ings} price={this.state.totalPrice} purchaseCancelled={this.purchaseCancelHandler}purchaseContinued={this.purchaseContinueHandler}/>
            );
        }
        if(this.state.loading){
            Ordersummary = <Spinner />;
        }
        console.log("Burger Builder");
        return (
            <Aux>
                <Modal 
                show={this.state.purchasing}
                modalClosed={this.purchaseCancelHandler}>
                    {Ordersummary}
                </Modal>
                {burger}
            </Aux>
        );
    }
}

const mapStateToProps = state =>{
    return {
        ings:state.ingredients,
        price:state.totalPrice
    }
}

const mapDispatchToProps = dispatch =>{
    return {
        onIngredientAdded:(ingName) =>dispatch({type:actionTypes.ADD_INGREDIENT,ingredientName:ingName}),
        onIngredientRemoved:(ingName) =>dispatch({type:actionTypes.REMOVE_INGREDIENT,ingredientName:ingName})
    }
}


export  default connect(mapStateToProps,mapDispatchToProps)(withErrorHandler(BurgerBuilder,axios));
