import * as actionTypes from './actionTypes';
import axios from 'axios';

export const authStart = () =>{
    return {
        type:actionTypes.AUTH_START
    };
};

export const authSuccess = (token,userId) =>{
    return {
        type:actionTypes.AUTH_SUCCESS,
        token:token,
        userId:userId
    };
};

export const authFail = (error) =>{
    return {
        type:actionTypes.AUTH_FAIL,
        error:error
    };
};

export const auth =(email,password,isSignup) =>{
    return dispatch =>{
        dispatch(authStart());
        const authData={
            email:email,
            password:password,
            returnSecureToken:true
        }
        let url = 'https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyB82NNpMRmyiLHdvtZMp5IeuzpwZHibc9w';
        if(!isSignup){
            url = 'https://identitytoolkit.googleapis.com/v1/accounts:signInWithCustomToken?key=AIzaSyB82NNpMRmyiLHdvtZMp5IeuzpwZHibc9w';
        }
        axios.post(url,authData).then(response =>{
            dispatch(authSuccess(response.data));
        }).catch(err =>
            dispatch(authFail(err))
        );
    }
}
