import * as actionTypes from './actionTypes';
import axios from 'axios';

export const authStart = () =>{
    return {
        type:actionTypes.AUTH_START
    };
};

export const authSuccess = (token,userId) =>{
    return {
        type:actionTypes.AUTH_SUCCESS,
        idToken:token,
        userId:userId
    };
};

export const authFail = (error) =>{
    return {
        type:actionTypes.AUTH_FAIL,
        error:error
    };
};

export const checkAuthTimeout = expirationTime =>{
    return dispatch =>{
        dispatch(authSuccess(response.data.idToken,response))
    };
};

export const auth =(email,password,isSignup) =>{
    return dispatch =>{
        dispatch(authStart());
        const authData={
            email:email,
            password:password,
            returnSecureToken:true,
        }
        let url = 'https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyB82NNpMRmyiLHdvtZMp5IeuzpwZHibc9w';
        if(!isSignup){
            url ='https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyB82NNpMRmyiLHdvtZMp5IeuzpwZHibc9w';
        }
        axios.post(url,authData).then(response =>{
            console.log(response.data);
            dispatch(authSuccess(response.data.idToken,response.data.localId));
        }).catch(err =>
            dispatch(authFail(err.response.data.error))
        );
    }
}
